      <Popup
        visible={visible}
        close={close}
        position='bottom'
      >
        <div className={clazz} {...rest}>
          <div className={headerClazz}>
            <a onClick={this._onCancel}>{cancelText}</a>
            <span>{title}</span>
            <a onClick={this._onConfirm}>{confirmText}</a>
          </div>
          <InlineDatePicker
            selectedDate={this.state.selectedDate}
            minDate={minDate}
            maxDate={maxDate}
            yearUnit={yearUnit}
            monthUnit={monthUnit}
            dateUnit={dateUnit}
            onChange={this._onChange}
          />
        </div>
      </Popup>
    );
  }
}

