  onChange: PropTypes.func,
  close: PropTypes.func,
  labelName: PropTypes.string,
  nextName: PropTypes.string,
  className: PropTypes.string
};

Picker.defaultProps = {
  confirmText: '确定',
  cancelText: '取消'
};

