    onChange && onChange(selectedTime);
  };

  _onCancel = (e) => {
    const {
      close,
      onCancel
    } = this.props;

    close && close();
    onCancel && onCancel(this.state.selectedTime, e);
  };

  _onConfirm = (e) => {
    const {
      close,
      onConfirm
    } = this.props;

    close && close();
    onConfirm && onConfirm(this.state.selectedTime, e);
  };

  render() {
    const {
      visible,
      title = this.state.selectedTime,
      selectedTime,
      onConfirm,
      onCancel,
      confirmText,
      cancelText,
      maxTime,
      minTime,
      hourUnit,
      minuteUnit,
      secondUnit,
      close,
      className,
      ...rest
    } = this.props;
    const clazz = classNames(prefix, {
      [className]: className
    });
    const headerClazz = classNames(`${prefix}-header`);

    return (
      <Popup
        visible={visible}
        close={close}
        position='bottom'
      >
        <div className={clazz} {...rest}>
          <div className={headerClazz}>
            <a onClick={this._onCancel}>{cancelText}</a>
            <span>{title}</span>
            <a onClick={this._onConfirm}>{confirmText}</a>
          </div>
          <InlineTimePicker
            selectedTime={this.state.selectedTime}
            minTime={minTime}
            maxTime={maxTime}
            hourUnit={hourUnit}
            minuteUnit={minuteUnit}
            secondUnit={secondUnit}
            onChange={this._onChange}
          />
        </div>
      </Popup>
    );
  }
}

